import { useEffect } from 'react';
import './App.css';
import io from 'socket.io-client';

function App() {

  useEffect(() => {
    const socket = io("/");

    return () => {
      socket.disconnect(); // Clean up the socket connection when the component is unmounted
    };
  }, []);

  return (
    <div className="App">
      app works
    </div>
  );
}

export default App;
